import e from"node:crypto";import{createHashBuffer as t,randomBytes as a}from"./crypto.mjs";describe("createHash",(()=>{it("should create a hash of the message using SHA256",(async()=>{let a="Hello, crypto!",o=await t(a);expect(o).toHaveLength(32);let r=Array.from(o),s=e.createHash("sha256").update(a).digest().toJSON().data;expect(r).toEqual(s)}))})),describe("randomBytes",(()=>{it("should create random bytes of the specified length",(()=>{expect(a(32).length).toEqual(32)})),it("should create different random bytes each time",(()=>{for(let e=0;e<20;e++){let[e,t]=[a(32),a(32)];expect(e).not.toEqual(t)}}))}));
